import { Component } from '@angular/core';
import { TemasService } from '../../services/temas.service';
import { Tema } from 'src/models/Tema';
import { ParteTema } from 'src/models/ParteTema';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {

  temas!: Tema[];
  partes!: ParteTema[];
  temaActive!: Tema;

  constructor(
    private temasService: TemasService,
    priva
  ) {
    this.temasService.getTemas().subscribe((resp: Tema[]) => {
      console.log(resp);
      this.temas = resp;
      this.temaActive = this.temas[0];
    });
    this.temasService.getPartesTema(3).subscribe((resp: ParteTema[]) => {
      console.log(resp);
      this.partes = resp;  
    });

  }

  saludar(){
    console.log("Hola");

  }

  mostrar(tema: Tema){
    console.log(tema.id_tema);
    console.log(tema.nombre_usuario);
    console.log(tema.titulo);

  }



}
